-------------------------- TABLE 4 -------------------------------

select sil.[Document No_],
	   sil.[Line Item ID],
	   sil.[Description 3],
       sil.[Contract ID],
       s.[CRM Location ID] as SellCRMID,
       sil.[Description], sil.[Business Unit Code],
       sum(sil.amount) as 'PreTaxAmount',
       sum(case when sil.[Business Unit Code] = 'COMPS' then sil.amount else 0 end) as 'Comps Professional',
       0 as 'Comps Professional Credits',
       sum(case when sil.[Business Unit Code] = 'CROSTRACW' then sil.amount else 0 end) as 'Tenant',
       0 as 'Tenant Credits',
       sum(case when sil.[Business Unit Code] = 'COSTARWEB' then sil.amount else 0 end) as 'Property Professional',
       0 as 'Property Professional Credits',
       sum(case when sil.[Business Unit Code] = 'CMAUS' then sil.amount else 0 end) as 'CMA',
       0 as 'CMA Credits',
       sum(case when sil.[Business Unit Code] = 'CONNECT' then sil.amount else 0 end) as 'CONNECT',
       0 as 'CONNECT Credits',
       cast(0.00000 as decimal(12,4)) as 'DocumentTotal',
       cast(0.00000 as decimal(30,20)) as 'PercentOfDocTot',
       '' as 'CreditMemoNo',
       cast(0.00000 as decimal(12,4)) as 'TotalInvoicesOnCreditMemo',
       cast(0.00000 as decimal(30,20)) as 'PercentOnCreditMemo'
INTO ##invoiceCredits
from [rig$sales invoice line] sil (nolock) 
	join [rig$customer] s (nolock) 
		on sil.[sell-to customer no_] = s.[no_]
	join ##creditedinvs ci 
		on sil.[Document No_] = ci.[AppliesDocNo]
group by sil.[Document No_],
	     sil.[Line Item ID], 
	     sil.[Description 3],
		 sil.[Contract ID],
		 sil.CustAccountLocationID,
		 s.[CRM Location ID], --as Sell
		 sil.[Description], 
		 sil.[Business Unit Code]


UPDATE i 
set 
	[DocumentTotal] = i2.DocumentTotal 
from ##invoiceCredits i
join (
	  select [Document No_], cast(sum([PreTaxAmount]) as decimal(12,4)) as [DocumentTotal] 
	  from ##invoiceCredits i
	  group by i.[Document No_]
	 ) i2 on i.[Document No_] = i2.[Document No_]


UPDATE i 
set 
	PercentOfDocTot = [PreTaxAmount] / [DocumentTotal] 
from ##invoiceCredits i


UPDATE i 
set 
	[DocumentTotal] = i2.DocumentTotal 
from ##appinvs i
join (
		select [Document No_], cast(sum([PreTaxAmount]) as decimal(12,4)) as [DocumentTotal] 
		from ##invoiceCredits i
		group by i.[Document No_]
	) i2 on i.AppliesDocNo = i2.[Document No_]


UPDATE i 
set TotalInvoicesOnCreditMemo = i2.TotalInvoicesOnCreditMemo 
from ##appinvs i
join (
		select CreditDocumentNo, 
		       cast(sum([DocumentTotal]) as decimal(12,4)) as TotalInvoicesOnCreditMemo 
		from ##appinvs
		group by CreditDocumentNo
	) i2 on i.CreditDocumentNo = i2.CreditDocumentNo


