-------------------------- TABLE 5 -------------------------------

select * 
into ##creditsNoBUcode
from ##credits c
where [Business Unit Code] is null or [Business Unit Code] = ''
order by [Line Item ID], 
         [Document No_]



select ic.[Document No_],
	   cnobu.HQLocID,
	   ic.[Business Unit Code], ic.PreTaxAmount,
	   ai.CreditDocumentNo, ai.AppliesDocNo,
	   ai.[DocumentTotal], ai.TotalInvoicesOnCreditMemo,
	   ic.PreTaxAmount / ai.TotalInvoicesOnCreditMemo as CreditPercent,
	   cnobu.pretaxamount * (ic.PreTaxAmount / ai.TotalInvoicesOnCreditMemo) as BUCreditAmt,
	   cnobu.pretaxamount as FullCreditAmt 
into ##FinalNoBUcredits
from ##invoiceCredits ic
	join ##appinvs ai 
		on ic.[Document No_] = ai.AppliesDocNo
	join ##creditsNoBUcode cnobu 
		on ai.CreditDocumentNo = cnobu.[document no_]
where ic.[Business Unit Code] in ('COMPS', 'CROSTRACW', 'CROSTRAC', 'COSTARWEB', 'CMAUS', 'COSTAR')



select * 
into ##FinalHasBUcredits
from ##credits
where [Business Unit Code] is not null and [Business Unit Code] <> ''
 and [Business Unit Code] in ('COMPS', 'CROSTRACW', 'CROSTRAC', 'COSTARWEB', 'CMAUS', 'COSTAR')
order by 2



