		, sum(sil.amount) as 'PreTaxAmount'
		--, sum(sil.[Amount Including VAT]) as 'Amount w-VAT', sum(sil.[line amount]) as 'Line Amount'
		, sum(case when sil.[Business Unit Code] = 'COMPS' then sil.amount else 0 end) as 'Comps Professional'
		, 0 as 'Comps Professional Credits'
		, sum(case when sil.[Business Unit Code] = 'CROSTRACW' then sil.amount else 0 end) as 'Tenant'
		, 0 as 'Tenant Credits'
		--, sum(case when sil.[Business Unit Code] = 'CROSTRAC' then sil.amount else 0 end) as 'Tenant'
		, sum(case when sil.[Business Unit Code] = 'COSTARWEB' then sil.amount else 0 end) as 'Property Professional'
		, 0 as 'Property Professional Credits'
		, sum(case when sil.[Business Unit Code] = 'CMAUS' then sil.amount else 0 end) as 'CMA'
		, 0 as 'CMA Credits'
		--, sum(case when sil.[Business Unit Code] = 'COSTAR' then sil.amount else 0 end) as 'COSTAR'
		, sum(case when sil.[Business Unit Code] = 'CONNECT' then sil.amount else 0 end) as 'CONNECT'
		, 0 as 'CONNECT Credits'
		, cast(0.00000 as decimal(12,4)) as 'DocumentTotal'
		, cast(0.00000 as decimal(30,20)) as 'Percent'
	INTO ##invoices
	from [rig$sales invoice line] sil (nolock) 
	join [rig$customer] s (nolock) on sil.[sell-to customer no_] = s.[no_]
	join ##Contracts c (nolock) on s.[CRM Location ID] = c.LocationID --s.[Parent Company ID]  = c.[HQLocID] and sil.[Contract ID] = c.ContractID
	where 
		sil.[Business Unit Code] in ('COMPS', 'CROSTRACW', 'CROSTRAC', 'COSTARWEB', 'CMAUS', 'COSTAR'
		--, 'CONNECT'
		)
		and 
		--sil.[Posting Date] between '2015-12-01' and '2016-11-30'
		--and sil.[Posting Date] between '2016-11-01' and '2016-11-30'
		sil.[Posting Date] between '2017-01-01' and '2017-01-31'
	group by 
		sil.[Document No_]
		, sil.[Line Item ID], sil.[Description 3]
		, sil.[Contract ID]
		, sil.CustAccountLocationID
	, s.[CRM Location ID] --as Sell
		, c.HQLocID
		, sil.Description, sil.[Business Unit Code]

UPDATE i 
set 
	[DocumentTotal] = i2.DocumentTotal 
from ##invoices i
join (
		select [Document No_], cast(sum([PreTaxAmount]) as decimal(12,4)) as [DocumentTotal] --, ([PreTaxAmount] / sum([PreTaxAmount])) as [Percent] 
		from ##invoices i
		group by i.[Document No_]
	) i2 on i.[Document No_] = i2.[Document No_]

UPDATE i 
set 
	[Percent] = (case when [DocumentTotal] = 0 then 0 else [PreTaxAmount] / [DocumentTotal] end) --[PreTaxAmount] / sum(i.[PreTaxAmount]) OVER (PARTITION BY i.[Document No_]) 
from ##invoices i


